DROP TABLE IF EXISTS `cake_crawl_releases`;
CREATE TABLE IF NOT EXISTS `cake_crawl_releases` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(200) DEFAULT '',
  `cid` int(11) DEFAULT '0',
  `creator` int(13) DEFAULT '0',
  `lastupdator` int(13) DEFAULT '0',
  `model_to` varchar(200) DEFAULT NULL,
  `siteid` int(11) DEFAULT '0',
  `deleted` tinyint(1) DEFAULT '0',
  `created` datetime DEFAULT NULL,
  `updated` datetime DEFAULT NULL,
  `crawl_id` int(10) DEFAULT '0',
  `serialize_info` text,
  PRIMARY KEY (`id`)
) ENGINE=MYISAM  DEFAULT CHARSET=utf8;



