  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(5000) DEFAULT NULL,
  `cate_id` bigint(11) DEFAULT NULL,
  `creator` varchar(60) DEFAULT NULL,
  `lastupdator` int(11) DEFAULT '0',
