  `content` text,
  `coverimg` varchar(200) DEFAULT NULL,
  `replytotrans` tinyint(1) DEFAULT NULL,
  `allowrepeat` tinyint(1) DEFAULT NULL,
  `join_num` int(11) DEFAULT NULL,
  `needinvite` tinyint(1) DEFAULT NULL,
  `weibo_id` bigint(13) DEFAULT NULL,
  `creator_id` varchar(13) DEFAULT NULL,
  `favor_nums` bigint(11) DEFAULT NULL,
  `comment_nums` bigint(11) DEFAULT NULL,
  `user_img` varchar(200) DEFAULT NULL,
  `inviteusers` varchar(200) DEFAULT NULL,
  `view_nums` bigint(11) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8;
