DROP TABLE IF EXISTS `cake_categories`;
CREATE TABLE IF NOT EXISTS `cake_categories` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(200) DEFAULT NULL,
  `parent_id` bigint(11) DEFAULT '0',
  `type` varchar(30) DEFAULT NULL,
  `left` int(11) DEFAULT NULL,
  `right` int(11) DEFAULT NULL,
  `slug` varchar(200) DEFAULT NULL,
  `visible` tinyint(1) DEFAULT '0',
  `photo` varchar(255) DEFAULT NULL,
  `banner` varchar(255) DEFAULT NULL,
  `description` varchar(200) DEFAULT NULL,
  `seotitle` varchar(255) DEFAULT NULL,
  `seokeywords` varchar(255) DEFAULT NULL,
  `seodescription` varchar(255) DEFAULT NULL,
  `link` varchar(20) DEFAULT NULL,
  `published` tinyint(1) DEFAULT '0',
  `deleted` tinyint(1) DEFAULT '0',
  `updated` datetime DEFAULT NULL,
  `created` datetime DEFAULT NULL,
  `content` text,
  `locale` varchar(10) DEFAULT NULL,
  `model` varchar(60) DEFAULT NULL,
  `model_cate_id` int(10) DEFAULT '0',
  `template` varchar(60) DEFAULT NULL,
  `submenu` text,
  `logo` varchar(200) DEFAULT NULL,
  `domain` varchar(30) DEFAULT NULL,
  `theme` varchar(20) DEFAULT NULL,
  `view_template` varchar(20) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `slug` (`slug`)
) ENGINE=MYISAM  DEFAULT CHARSET=utf8;
