DROP TABLE IF EXISTS `cake_crawls`;
CREATE TABLE IF NOT EXISTS `cake_crawls` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(60) DEFAULT NULL,
  `model_to` varchar(30) DEFAULT NULL,
  `targeturl` text,
  `urlcontains` varchar(255) DEFAULT NULL,
  `targetcharset` varchar(20) DEFAULT NULL,
  `pages` varchar(200) DEFAULT NULL,
  `category_id` varchar(200) DEFAULT NULL,
  `imgprefix` varchar(20) DEFAULT NULL,
  `saveimg` tinyint(1) DEFAULT NULL,
  `regexp_xml` text,
  `updated` datetime DEFAULT NULL,
  `created` datetime DEFAULT NULL,
  `urlcontent_regexp` varchar(1000) DEFAULT NULL,
  `datatype` varchar(20) DEFAULT NULL,
  `autopublished` tinyint(1) DEFAULT '0',
  `origin` varchar(200) DEFAULT NULL,
  `urlnotcontains` varchar(200) DEFAULT NULL,
  `contentreplace` text,
  `creator` int(11) DEFAULT '0',
  `deleted` tinyint(1) NOT NULL DEFAULT '0',
  `urltextnotcontains` varchar(200) DEFAULT NULL,
  `cate_id` int(10) DEFAULT '0',
  `urltextcontains` varchar(200) DEFAULT NULL,
  `published` tinyint(1) DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MYISAM  DEFAULT CHARSET=utf8;



