DROP TABLE IF EXISTS `cake_products`;
CREATE TABLE IF NOT EXISTS `cake_products` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_id` int(11) DEFAULT '0',
  `name` varchar(250) DEFAULT NULL,
  `coverimg` varchar(255) DEFAULT NULL,
  `slug` varchar(200) DEFAULT NULL,
  `color` varchar(50) DEFAULT NULL,
  `material` varchar(50) DEFAULT NULL,
  `manufacturer` varchar(64) DEFAULT NULL,
  `price` float DEFAULT '0',
  `special` float DEFAULT '0',
  `manual` text,
  `content` text,
  `remoteurl` varchar(255) DEFAULT NULL,
  `status` tinyint(1) DEFAULT '0',
  `deleted` tinyint(1) DEFAULT '0',
  `priority` bigint(11) DEFAULT '0',
  `views_count` int(11) DEFAULT '0',
  `saled` int(8) DEFAULT '0',
  `storage` bigint(10) DEFAULT '0',
  `seotitle` char(255) DEFAULT '',
  `seodescription` char(255) DEFAULT '',
  `seokeywords` char(255) DEFAULT '',
  `created` datetime DEFAULT NULL,
  `updated` datetime DEFAULT NULL,
  `published` tinyint(1) NOT NULL DEFAULT '0',
  `brand_id` bigint(11) DEFAULT '0',
  `photo` varchar(100) DEFAULT NULL,
  `cate_id` int(10) DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8;



