!function(window,angular){function IndexCtrl($scope,$rootScope,$http,$log,$attrs,Utils){function activate(){$rootScope.showUnReadMark=!1,$rootScope.proxies=[],$rootScope.loadingPage=!1,vm.uid=-1,$rootScope.checkHasUnRead(),$http.get("/users/get_id_and_proxies").success(function(data){null!=data.uid&&($rootScope.uid=data.uid,$rootScope.proxies=_.map(data.proxies,function(pid){return parseInt(pid)}))}).error(function(data,e){$log.log("Failed to get proxies: "+e)});var tag=$attrs.tag;vm.indexProducts=[],$http.get("/index_products/index_products/"+tag).success(function(data){vm.indexProducts=_.map(data,function(p){return{id:p.IndexProduct.id,shareId:p.IndexProduct.share_id,summary:p.IndexProduct.summary}})}).error(function(data,e){$log.log("Failed to get index products: "+e)})}function getSummary(shareId){var indexProduct=_.find(vm.indexProducts,function(p){return p.shareId==shareId});return _.isEmpty(indexProduct)?{orders_count:0,view_count:0,orders_and_creators:[]}:indexProduct.summary}var vm=this;vm.staticFilePath=Utils.staticFilePath(),vm.getSummary=getSummary,activate()}angular.module("weshares").controller("IndexCtrl",IndexCtrl)}(window,window.angular);